import React, { useState } from 'react';
import { Box, Button, MenuItem, Select, TextField, Typography, Checkbox, FormControlLabel, Dialog, DialogTitle, DialogContent, DialogActions, List, ListItem, IconButton, Divider, } from '@mui/material';
import { v4 as uuidv4 } from 'uuid';
import { SvgIcon } from '@mui/material';

const DeleteIcon = (props: any) => (
    <SvgIcon {...props}>
        <path d="M6 19c0 1.1.9 2 2 2h8c1.1 0 2-.9 2-2V7H6v12zM19 4h-3.5l-1-1h-5l-1 1H5v2h14V4z"/>
    </SvgIcon>
);

type FieldType = 'text' | 'number' | 'textarea' | 'select' | 'radio' | 'checkbox' | 'date';

interface Validation {
    required: boolean;
    minLength?: number;
    maxLength?: number;
    email?: boolean;
    password?: boolean;
}

interface Field {
    id: string;
    type: FieldType;
    label: string;
    defaultValue?: string;
    options?: string[];
    validation: Validation;
    derived?: boolean;
    parentFields?: string[];
    formula?: string;
}

const CreateForm: React.FC = () => {
    const [fields, setFields] = useState<Field[]>([]);
    const [newField, setNewField] = useState<FieldType>('text');
    const [formName, setFormName] = useState('');
    const [openDialog, setOpenDialog] = useState(false);

    const addField = () => {
        const id = uuidv4();
        const field: Field = {
            id,
            type: newField,
            label: '',
            validation: { required: false },
            options: newField === 'select' ? [] : undefined,
        };

        if (['select', 'radio', 'checkbox'].includes(newField)) {
            field.options = ['Option 1', 'Option 2'];
        }

        setFields((prev) => [...prev, field]);
    }

    const updateField = (id: string, updated: Partial<Field>) => {
        setFields((prev) =>
            prev.map((field) => (field.id === id ? { ...field, ...updated } : field))
        );
    };

    const deleteField = (id: string) => {
        setFields((prev) => prev.filter((field) => field.id !== id));
    };


    return (
        <Box p={3}>
            <h2>Create Dynamic Form</h2>
            <Box display="flex" gap={2} alignItems="center" mb={3}>
                <Select value={newField} onChange={(e) => setNewField(e.target.value as FieldType)}>
                    <MenuItem value="text">Text</MenuItem>
                    <MenuItem value="number">Number</MenuItem>
                    <MenuItem value="textarea">Textarea</MenuItem>
                    <MenuItem value="select">Select</MenuItem>
                    <MenuItem value="radio">Radio</MenuItem>
                    <MenuItem value="checkbox">Checkbox</MenuItem>
                    <MenuItem value="date">Date</MenuItem>
                </Select>
                <Button variant="contained" onClick={addField}>
                    Add Field
                </Button>
            </Box>
            <List>
                {fields.map((field, index) => (
                    <Box key={field.id} mb={3}>
                        <ListItem>
                            <Box width="100%">
                                <Typography variant="h6">Field #{index + 1}</Typography>

                                <TextField
                                    fullWidth
                                    label="Label"
                                    value={field.label}
                                    onChange={(e) => updateField(field.id, { label: e.target.value })}
                                    margin="normal"
                                />

                                <TextField
                                    fullWidth
                                    label="Default Value"
                                    value={field.defaultValue}
                                    onChange={(e) => updateField(field.id, { defaultValue: e.target.value })}
                                    margin="normal"
                                />

                                <FormControlLabel
                                    control={
                                        <Checkbox
                                            checked={field.validation.required}
                                            onChange={(e) =>
                                                updateField(field.id, {
                                                    validation: {
                                                        ...field.validation,
                                                        required: e.target.checked,
                                                    },
                                                })
                                            }
                                        />
                                    }
                                    label="Required"
                                />
                                <br/>

                                {field.type === 'text' || field.type === 'textarea' ? (
                                    <>
                                        <TextField
                                            type="number"
                                            label="Min Length"
                                            value={field.validation.minLength || ''}
                                            onChange={(e) =>
                                                updateField(field.id, {
                                                    validation: {
                                                        ...field.validation,
                                                        minLength: Number(e.target.value),
                                                    },
                                                })
                                            }
                                            margin="normal"
                                        />
                                        <TextField
                                            type="number"
                                            label="Max Length"
                                            value={field.validation.maxLength || ''}
                                            onChange={(e) =>
                                                updateField(field.id, {
                                                    validation: {
                                                        ...field.validation,
                                                        maxLength: Number(e.target.value),
                                                    },
                                                })
                                            }
                                            margin="normal"
                                        />
                                        <br/>
                                        <FormControlLabel
                                            control={
                                                <Checkbox
                                                    checked={field.validation.email || false}
                                                    onChange={(e) =>
                                                        updateField(field.id, {
                                                            validation: {
                                                                ...field.validation,
                                                                email: e.target.checked,
                                                            },
                                                        })
                                                    }
                                                />
                                            }
                                            label="Email Format"
                                        />
                                        <FormControlLabel
                                            control={
                                                <Checkbox
                                                    checked={field.validation.password || false}
                                                    onChange={(e) =>
                                                        updateField(field.id, {
                                                            validation: {
                                                                ...field.validation,
                                                                password: e.target.checked,
                                                            },
                                                        })
                                                    }
                                                />
                                            }
                                            label="Password Rule"
                                        />
                                    </>
                                ) : null}

                                {/* Derived field options */}
                                <FormControlLabel
                                    control={
                                        <Checkbox
                                            checked={field.derived || false}
                                            onChange={(e) =>
                                                updateField(field.id, { derived: e.target.checked })
                                            }
                                        />
                                    }
                                    label="Is Derived Field?"
                                />

                                {field.derived && (
                                    <>
                                        <TextField
                                            fullWidth
                                            label="Parent Field IDs (comma separated)"
                                            value={field.parentFields?.join(',') || ''}
                                            onChange={(e) =>
                                                updateField(field.id, {
                                                    parentFields: e.target.value.split(',').map((s) => s.trim()),
                                                })
                                            }
                                            margin="normal"
                                        />
                                        <TextField
                                            fullWidth
                                            label="Formula / Logic (e.g., calcAge(dob))"
                                            value={field.formula || ''}
                                            onChange={(e) => updateField(field.id, { formula: e.target.value })}
                                            margin="normal"
                                        />
                                    </>
                                )}
                            </Box>

                            <IconButton onClick={() => deleteField(field.id)}>
                                <DeleteIcon />
                            </IconButton>
                        </ListItem>
                        <Divider />
                    </Box>
                ))}
            </List>

            <Button variant="contained" color="primary">
                Save Form
            </Button>

        </Box>
    );
};

export default CreateForm;
