import React, { useEffect, useState } from 'react';
import {
  Box,
  TextField,
  Typography,
  Checkbox,
  FormControlLabel,
  Select,
  MenuItem,
  RadioGroup,
  Radio,
  FormHelperText,
  Button,
} from '@mui/material';

interface Validation {
  required: boolean;
  minLength?: number;
  maxLength?: number;
  email?: boolean;
  password?: boolean;
}

interface Field {
  id: string;
  type: string;
  label: string;
  defaultValue?: string;
  options?: string[];
  validation: Validation;
  derived?: boolean;
  parentFields?: string[];
  formula?: string;
}

interface FormSchema {
  name: string;
  fields: Field[];
}

const PreviewForm: React.FC = () => {
  const [schema, setSchema] = useState<FormSchema | null>(null);
  const [values, setValues] = useState<Record<string, any>>({});
  const [errors, setErrors] = useState<Record<string, string>>({});

  useEffect(() => {
    // Load most recently saved form
    const keys = Object.keys(localStorage).filter((key) => key.startsWith('form_'));
    if (keys.length === 0) return;
    const lastKey = keys[keys.length - 1];
    const stored = localStorage.getItem(lastKey);
    if (stored) {
      const parsed = JSON.parse(stored) as FormSchema;
      setSchema(parsed);

      // Set default values
      const defaults: Record<string, any> = {};
      parsed.fields.forEach((f) => {
        defaults[f.id] = f.defaultValue || '';
      });
      setValues(defaults);
    }
  }, []);

  useEffect(() => {
  if (!schema) return;

  let updated = { ...values };
  let hasChanged = false;

  schema.fields.forEach((field) => {
    if (field.derived && field.parentFields?.length && field.formula) {
      const context = field.parentFields.reduce((acc, id) => {
        acc[id] = values[id];
        return acc;
      }, {} as Record<string, any>);

      try {
        const computedValue = evalFormula(field.formula!, context);
        if (values[field.id] !== computedValue) {
          updated[field.id] = computedValue;
          hasChanged = true;
        }
      } catch {
        updated[field.id] = 'Error';
        hasChanged = true;
      }
    }
  });

  if (hasChanged) {
    setValues(updated); // âœ… Only update if something actually changed
  }
}, [schema, ...Object.values(values)]);


  const evalFormula = (formula: string, context: Record<string, any>) => {
    const safeKeys = Object.keys(context).map((key) => `const ${key} = ${JSON.stringify(context[key])};`).join('');
    const func = new Function(`${safeKeys} return ${formula};`);
    return func();
  };

  const validateField = (field: Field, value: any): string => {
    const { validation } = field;
    if (validation.required && !value) return 'This field is required.';
    if (validation.minLength && value.length < validation.minLength)
      return `Minimum length is ${validation.minLength}`;
    if (validation.maxLength && value.length > validation.maxLength)
      return `Maximum length is ${validation.maxLength}`;
    if (validation.email && !/^\S+@\S+\.\S+$/.test(value)) return 'Invalid email format.';
    if (
      validation.password &&
      (!/^.{8,}$/.test(value) || !/\d/.test(value))
    )
      return 'Password must be at least 8 characters and contain a number.';
    return '';
  };

  const handleChange = (id: string, value: any) => {
    setValues((prev) => ({ ...prev, [id]: value }));
    const field = schema?.fields.find((f) => f.id === id);
    if (field) {
      const err = validateField(field, value);
      setErrors((prev) => ({ ...prev, [id]: err }));
    }
  };

  const handleSubmit = () => {
    const newErrors: Record<string, string> = {};
    schema?.fields.forEach((field) => {
      if (!field.derived) {
        const err = validateField(field, values[field.id]);
        if (err) newErrors[field.id] = err;
      }
    });

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      alert('Form submitted successfully (not actually saved)!');
    }
  };

  if (!schema) return <Typography>Loading form...</Typography>;

  return (
    <Box p={4}>
      <Typography variant="h4" gutterBottom>
        Preview: {schema.name}
      </Typography>

      <Box component="form" display="flex" flexDirection="column" gap={3}>
        {schema.fields.map((field) => {
          const value = values[field.id] ?? '';
          const error = errors[field.id] ?? '';

          switch (field.type) {
            case 'text':
            case 'number':
            case 'date':
              return (
                <TextField
                  key={field.id}
                  type={field.type}
                  label={field.label}
                  value={value}
                  onChange={(e) => handleChange(field.id, e.target.value)}
                  error={!!error}
                  helperText={error}
                  disabled={field.derived}
                />
              );

            case 'textarea':
              return (
                <TextField
                  key={field.id}
                  label={field.label}
                  value={value}
                  onChange={(e) => handleChange(field.id, e.target.value)}
                  error={!!error}
                  helperText={error}
                  multiline
                  rows={3}
                  disabled={field.derived}
                />
              );

            case 'select':
              return (
                <Box key={field.id}>
                  <Typography>{field.label}</Typography>
                  <Select
                    fullWidth
                    value={value}
                    onChange={(e) => handleChange(field.id, e.target.value)}
                    disabled={field.derived}
                  >
                    {field.options?.map((opt, i) => (
                      <MenuItem key={i} value={opt}>
                        {opt}
                      </MenuItem>
                    ))}
                  </Select>
                  {error && <FormHelperText error>{error}</FormHelperText>}
                </Box>
              );

            case 'radio':
              return (
                <Box key={field.id}>
                  <Typography>{field.label}</Typography>
                  <RadioGroup
                    value={value}
                    onChange={(e) => handleChange(field.id, e.target.value)}
                  >
                    {field.options?.map((opt, i) => (
                      <FormControlLabel key={i} value={opt} control={<Radio />} label={opt} />
                    ))}
                  </RadioGroup>
                  {error && <FormHelperText error>{error}</FormHelperText>}
                </Box>
              );

            case 'checkbox':
              return (
                <Box key={field.id}>
                  <Typography>{field.label}</Typography>
                  {field.options?.map((opt, i) => (
                    <FormControlLabel
                      key={i}
                      control={
                        <Checkbox
                          checked={(value || []).includes(opt)}
                          onChange={(e) => {
                            const checked = e.target.checked;
                            const newVal = [...(value || [])];
                            if (checked) newVal.push(opt);
                            else newVal.splice(newVal.indexOf(opt), 1);
                            handleChange(field.id, newVal);
                          }}
                        />
                      }
                      label={opt}
                    />
                  ))}
                </Box>
              );

            default:
              return null;
          }
        })}

        <Button variant="contained" onClick={handleSubmit}>
          Submit
        </Button>
      </Box>
    </Box>
  );
};

export default PreviewForm;
