import React, { useState } from 'react';
import { Container, AppBar, Toolbar, Typography, Button } from '@mui/material';
import FormBuilder from './components/FormBuilder';
import FormPreview from './components/FormPreview';
import FormList from './components/FormList';

const App: React.FC = () => {
    const [currentView, setCurrentView] = useState<'builder' | 'preview' | 'list'>('builder');
    const [formConfig, setFormConfig] = useState<any>(null);

    const handleFormSave = (config: any) => {
        setFormConfig(config);
        setCurrentView('preview');
    };

    const handleViewChange = (view: 'builder' | 'preview' | 'list') => {
        setCurrentView(view);
    };

    return (
        <Container>
            <AppBar position="static">
                <Toolbar>
                    <Typography variant="h6">Dynamic Form Builder</Typography>
                    <Button color="inherit" onClick={() => handleViewChange('builder')}>Builder</Button>
                    <Button color="inherit" onClick={() => handleViewChange('list')}>Saved Forms</Button>
                </Toolbar>
            </AppBar>
            {currentView === 'builder' && <FormBuilder onSave={handleFormSave} />}
            {currentView === 'preview' && <FormPreview formConfig={formConfig} />}
            {currentView === 'list' && <FormList onSelectForm={setFormConfig} />}
        </Container>
    );
};

export default App;
